mapper.delete:
DELETE
FROM plan_article
WHERE plan_article_id = #{planArticleId}
  AND group_id = #{groupId}

mapper.update:
UPDATE plan_article


    plan = #{plan}


    title = #{title}


    img_src = #{imgSrc}


WHERE plan_article_id = #{planArticleId}

mapper.select:
SELECT plan_article_id, plan, group_id, creation_date, uuid, img_src, plan_title
FROM plan_article
WHERE group_id = #{groupId}

SELECT uuid
FROM plan_article
WHERE plan_article_id = #{planArticleId}

SELECT group_id
FROM plan_article
WHERE plan_article_id = #{planArticleId}
