mapper.update:
update user


    password=#{password},


    nickname=#{nickname}


        profile_img=#{profileImgSerialized}


where uuid = #{uuid}

mapper.select:
select uuid, id, nickname, password, join_date, user_type, email, profile_img
from user
where id = #{id}

select uuid, id, nickname, password, join_date, user_type, email, profile_img
from user
where uuid = #{uuid}

select uuid, id, nickname, password, join_date, user_type, email, profile_img
from user
where id = #{id} and email = #{email}

select uuid, id, nickname, password, join_date, user_type, email, profile_img
from user
where email = #{email}
