mapper.update:
update user


    password=#{password},


    nickname=#{nickname}


where uuid = #{uuid}

mapper.select:
select uuid, id, nickname, password, join_date, user_type, email
from user
where id = #{id}

select uuid, id, nickname, password, join_date, user_type, email
from user
where uuid = #{uuid}

select uuid, id, nickname, password, join_date, user_type, email
from user
where id = #{id} and email = #{email}

select uuid, id, nickname, password, join_date, user_type, email
from user
where email = #{email}

select group_uuid
from travel_group
where uuid = #{uuid}

select uuid
from travel_group
where group_id = #{groupid}

mapper.update:
update travel_group
set group_master=true
where group_id = #{groupid} and uuid=#{uuid}
